// Loop over each input item
for (const item of $input.all()) {

  // 1) AI output 문자열 가져오기
  let raw = item.json.output;

  // 2) ```json ... ``` 제거
  raw = raw.replace(/```json/g, '')
           .replace(/```/g, '')
           .trim();

  // 3) 안전한 JSON 파싱
  let parsed;
  try {
    parsed = JSON.parse(raw);
  } catch (e) {
    parsed = {
      username: "unknown",
      type: "unknown",
      confidence: "low",
      reason: "JSON parsing error: " + e.message
    };
  }

  // 4) item.json 구조에 필드 저장
  item.json.username = parsed.username || "unknown";
  item.json.followers = parsed.followers || "unknown";
  item.json.profileURL = parsed.profileURL || "unknown";
  item.json.type = parsed.type || "unknown";
  item.json.confidence = parsed.confidence || "unknown";
  item.json.reason = parsed.reason || "no reason provided";

  // 5) 기존 output 제거
  delete item.json.output;
}

// 6) 모든 item 리턴
return $input.all();

for (const item of $input.all()) {

  // 1) error 필드가 존재하면 해당 아이템 제외
  if (item.json?.error) {
    continue;
  }

  // 2) output 이 없거나 문자열이 아니면 제외
  if (typeof item.json?.output !== 'string') {
    continue;
  }

  // 3) Raw output 가져오기
  let raw = item.json.output;

  // 4) replace() 실행 전 안전 체크
  if (typeof raw !== 'string') {
    continue; // 문자열이 아니면 제외
  }

  // ```json ... ``` 코드블록 제거
  raw = raw.replace(/```json/g, '')
           .replace(/```/g, '')
           .trim();

  // JSON 파싱
  let parsed;
  try {
    parsed = JSON.parse(raw);
  } catch (e) {
    parsed = {
      username: item.json.Username || "unknown",
      gender: "unknown",
      gender_confidence: "unknown",
      age_range: "unknown",
      age_confidence: "unknown",
      reason: "JSON parsing error: " + e.message
    };
  }

  // 기존 Username, ImageURL 유지
  item.json.Username = parsed.username || item.json.Username || "unknown";
  item.json.gender = parsed.gender || "unknown";
  item.json.gender_confidence = parsed.gender_confidence || "unknown";
  item.json.age_range = parsed.age_range || "unknown";
  item.json.age_confidence = parsed.age_confidence || "unknown";

  // output 필드는 제거
  delete item.json.output;
}

return $input.all();

