];

// 입력 전체 데이터
const items = $input.all();

// 첫 번째 아이템에서 username 목록 가져오기
const usernames = items[0].json.usernames;

// 나머지 아이템들은 릴스 데이터
const reelData = items.slice(1).map(i => i.json);

// username 기준으로 릴스 묶기
const grouped = {};

for (const username of usernames) {
  grouped[username] = [];
}

for (const reel of reelData) {
  if (grouped[reel.Username]) {
    grouped[reel.Username].push({
      viewCount: reel.ViewCount,
      playCount: reel.PlayCount,
      commentCount: reel.CommentCount
    });
  }
}

// 결과 구성
const result = [];

for (const username of usernames) {
  const reels = grouped[username];

  result.push({
    json: {
      username,
      hasReels: reels.length > 0,
      reelCount: reels.length,
      reels
    }
  });
}

return result;

