for (const item of $input.all()) {
  results.push({
    json: {
      username: item.json.Username,
      hashtag: item.json.Hashtag
    }
  });
}

return results;

const items = $input.all();

// 1) hashtag만 있는 간단 리스트 저장
const hashtagMap = {};
for (const item of items) {
  const d = item.json;
  if (d.hashtag && Object.keys(d).length === 2) {
    // username + hashtag만 있는 경우
    hashtagMap[d.username] = d.hashtag;
  }
}

// 2) 뒤쪽 데이터(해시태그 없는 상세 정보)에 hashtag 추가
const results = [];

for (const item of items) {
  const d = item.json;

  // followers 같은 상세 정보가 있는 데이터만 처리
  if (!d.hashtag && d.followers) {

    const username = d.username;
    const hashtag = hashtagMap[username];

    // hashtag 매칭 안되면 버림
    if (!hashtag) continue;

    // hashtag 삽입
    d.hashtag = hashtag;

    results.push({ json: d });
  }
}

return results;

const items = $input.all();
const result = [];

for (const item of items) {
  const data = item.json;

  const username = data.username;
  const reels = data.reels || [];

  // 추가 필드
  const followers = data.followers;
  const profileURL = data.profileURL;
  const type = data.type;
  const hashtag = data.hashtag;

  // ViewCount 계산
  const viewCounts = reels
    .map(r => r.viewCount)
    .filter(v => v !== null && v !== undefined);

  const avgView =
    viewCounts.length > 0
      ? viewCounts.reduce((a, b) => a + b, 0) / viewCounts.length
      : null;

  // PlayCount 계산
  const playCounts = reels
    .map(r => r.playCount)
    .filter(p => p !== null && p !== undefined);

  const avgPlay =
    playCounts.length > 0
      ? playCounts.reduce((a, b) => a + b, 0) / playCounts.length
      : null;

  // CommentCount 계산
  const commentCounts = reels
    .map(r => r.commentCount)
    .filter(v => v !== null && v !== undefined);

  const avgComment =
    commentCounts.length > 0
      ? commentCounts.reduce((a, b) => a + b, 0) / commentCounts.length
      : null;

  const maxPlay = playCounts.length > 0 ? Math.max(...playCounts) : null;
  const minPlay = playCounts.length > 0 ? Math.min(...playCounts) : null;
  const maxComment = commentCounts.length > 0 ? Math.max(...commentCounts) : null;
  const minComment = commentCounts.length > 0 ? Math.min(...commentCounts) : null;

  // 결과 추가
  result.push({
    json: {
      username,
      followers,
      profileURL,
      type,
      hashtag,

      averageViewCount: avgView,
      averagePlayCount: avgPlay,
      avgCommentCount: avgComment,

      maxPlayCount: maxPlay,
      minPlayCount: minPlay,
      maxCommentCount: maxComment,
      minCommentCount: minComment
    }
  });
}

return result;
